INSERT INTO `adverts` (`id`, `status`, `type_id`, `category_id`, `region_id`, `user_id`, `title`, `body`, `end_publication`, `created_at`, `updated_at`, `view`) VALUES
(1, 1, 1, 137, 1, 2, 'Продам картофель розовый (сорт Рэд Леди).', '<p>Продам картофель розовый <span style=\"background-color:#40E0D0;\">(сорт Рэд Леди)</span>. <span style=\"color:#FFA500;\">Оптом и крупным оптом</span>. От производителя. Херсонская обл., г. Каховка, ПП \"Шато\". тел. <strong>+38095509606</strong>4 зам. рук. Ирина Васильевна123</p>', 1482012000, 1479408641, 1500278617, 1),
(2, 10, 2, 40, 1, 3, 'Продам картоплю сорт БЕЛАРОССА у сітках 20кг.', '<p>Продам картоплю сорт БЕЛАРОССА у сітках 20кг. Чиста з піску. Без пошкоджень, дротяника та порші.</p>', 0000000000, 1479408641, 1480357903, 34),
(3, 10, 2, 18, 18, 2, 'Науково – виробнича фірма «ГРАН» Пропонуємо насіння соняшнику \"Дракон\" Ранньостиглий(103 – 107 дн)', '<p>Науково &ndash; виробнича фірма &laquo;ГРАН&raquo; Пропонуємо насіння соняшнику &quot;Дракон&quot; Ранньостиглий(103 &ndash; 107 дн), Урожайність в конкурсному випробуванні 39 ц/га Потенціал врожайності становить 47 ц/га Пропонуємо насіння соняшнику &quot;Матадор&quot; Середньостиглий(107 &ndash; 115 дн), Урожайність на конкурсному випробуванні 38,5 ц/га Потенціал врожайності становить 47 ц/га толерантний до гранстару Гібрид Оригінатор: Всеукраїнський науковий інститут селекції На випробуванні: з 2011 року Висота рослини 170-190 см, Діаметр кошику 21-23 см &bull; висока стійкість до вовчка (заразиха) - 6 рас та несправжньої борошнистої роси &bull; стійкий до вилягання &bull; висока стійкість до повітряної та ґрунтової посухи &bull; толерантний до фомопсису, до сірої та білої гнилей &bull; толерантний до ураження соняшникової молі та соняшникового усача Вміст олії 48 - 52 % Більш докладна інформація про сорти та гібриди соняшника знаходиться на нашому сайті: www.nvfgran.com.ua Люкс (100-105дн) Сорт Мир (90-95 дн) Сорт Українське сонечко (90&ndash;95 дн) Заграва (100 &ndash; 108 дн) Український F1 (105 &ndash; 108 дн) Сонячний настрій (98 &ndash; 103 дн) стійкий до гранстару Аттіла (95-100 дн) Ауріс (105-108 дн) стійкий до гранстару Армагедон (110 дн) стійкий до Євро-Лайтнінгу Бонд (105 &ndash; 110 дн) стійкий до гран стару Барса (110 &ndash; 116 дн) стійкий до гранстару Толедо (112-115 дн) стійкий до гранстару Нео (108-110 дн) стійкий до гранстару Рембо (120 дн) стійкий до гранстару</p>', 0000000000, 1479408641, 1480001784, 8),
(4, 10, 1, 12, 12, 2, '\"Продажа селитры аммиачной Производственное предприятие «КАС АГРОХИМ»', '<p>&quot;Продажа селитры аммиачной Производственное предприятие &laquo;КАС АГРОХИМ&raquo; реализует селитру аммиачную. Заказ от 1кг. Фасовка в мешках по 50 кг. Самовывоз. Услуги доставки. С уважением, директор ООО &laquo;ПП КАСАГРОХИМ&raquo;</p>', 0000000000, 2016, 1480017728, 5),
